from tkinter import *from Booking import bookingfrom Reservation import reservation, refresh_tablefrom PIL import Image, ImageTkdef home(root, screenwidth, screenheight):    # Canvas setup with background image    canvas = Canvas(root, width=screenwidth, height=screenheight)    canvas.pack(fill="both", expand=True)    bg_image = Image.open("images/background.jpg")    bg_image = bg_image.resize((screenwidth, screenheight))    bg_photo = ImageTk.PhotoImage(bg_image)    canvas.create_image(0, 0, anchor=NW, image=bg_photo)    canvas.bg_photo = bg_photo  # Prevent garbage collection    # Frames    booking_main_frame = booking(root, lambda: (canvas.pack(fill="both", expand=True), booking_main_frame.pack_forget()))    reservation_main_frame = reservation(root)    def show_home():        canvas.pack(fill="both", expand=True)        booking_main_frame.pack_forget()        reservation_main_frame.pack_forget()    def show_booking():        canvas.pack_forget()        reservation_main_frame.pack_forget()        booking_main_frame.pack(fill="both", expand=True)    def show_reservation():        canvas.pack_forget()        booking_main_frame.pack_forget()        reservation_main_frame.pack(fill="both", expand=True)        refresh_table()    # Text header    canvas.create_text(screenwidth / 2, 80, text="Welcome to FlySky Reservations", fill="#1a5276",                       font=("Segoe UI", 32, "bold"))    canvas.create_text(screenwidth / 2, 130,                       text="Easily book your flights and manage reservations through our clear system.",                       fill="#1a5276", font=("Segoe UI", 16))    # Booking card    canvas.create_rectangle(screenwidth / 4 - 150, screenheight / 2 - 20,                            screenwidth / 4 + 230, screenheight / 2 + 200,                            fill="#4682B4", outline="", stipple="gray50")    canvas.create_text(screenwidth / 4 +35, screenheight / 2 +20, text="Book a Flight",                       font=("Arial", 20, "bold"), fill="#1a5276")    canvas.create_text(screenwidth / 4 +38, screenheight / 2 +60,                       text="Reserve your next flight by entering your info.", font=("Segoe UI", 11), fill="black")    book_btn = Button(canvas, text="Book Flight", font=("Segoe UI", 11, "bold"), bg="#3393ff", fg="white",                      width=32, command=show_booking)    book_btn.place(x=screenwidth / 4 - 108, y=screenheight / 2 + 120)    # View Reservation card    canvas.create_rectangle(3 * screenwidth / 4 - 300, screenheight / 2 - 20,                            3 * screenwidth / 4 + 80, screenheight / 2 + 200,                            fill="#4682B4", outline="", stipple="gray50")    canvas.create_text(3 * screenwidth / 4 -110, screenheight / 2 +20, text="View Reservations",                       font=("Arial", 20, "bold"), fill="#1a5276")    canvas.create_text(3 * screenwidth / 4-110, screenheight / 2 +60,                       text="Check, edit, or cancel your reservations.", font=("Segoe UI", 11), fill="black")    view_btn = Button(canvas, text="View Reservations", font=("Segoe UI", 11, "bold"), bg="#3393ff", fg="white",                      width=32, command=show_reservation)    view_btn.place(x=3 * screenwidth / 4 - 258, y=screenheight / 2 + 120)    # Top bar buttons    home_btn = Button(root, text="Home", width=10, height=2,bg="#4682B4", command=show_home)    home_btn.place(x=screenwidth - 350, y=10)    top_book_btn = Button(root, text="Book", width=10, height=2,bg="#4682B4", command=show_booking)    top_book_btn.place(x=screenwidth - 250, y=10)    top_view_btn = Button(root, text="Reservations", width=15, height=2,bg="#4682B4", command=show_reservation)    top_view_btn.place(x=screenwidth - 150, y=10)    # Start home screen    booking_main_frame.pack_forget()  # Hide booking    reservation_main_frame.pack_forget()   #Hide reservation    canvas.pack(fill="both", expand=True)   # Show home    return canvas